Tree.propsAlt:
export type TreeProps<Item extends TreeItem, Multiple extends boolean = false> = {
  /**
   * If `true`, the user can select multiple items.
   * @default false
   */
  multiple?: MaybeGetter<Multiple | undefined>;
  /**
   * The currently selected item(s).
   * If `multiple` is `true`, this should be an `Iterable`.
   * Otherwise, it'll be a `string`.
   * @default undefined
   */
  selected?: MaybeMultiple<string, Multiple>;
  /**
   * Callback fired when selection changes
   * @param value - For multiple selection, a Set of selected IDs. For single selection, a single ID or undefined
   */
  onSelectedChange?: (value: Multiple extends true ? Set<string> : string | undefined) => void;
  /**
   * The currently expanded items
   *
   * @default undefined
   */
  expanded?: MaybeMultiple<string, true>;
  /**
   * Callback fired when expanded state changes
   * @param value - Set of expanded item IDs
   */
  onExpandedChange?: (value: Set<string>) => void;
  /**
   * If `true`, groups (items with children) expand on click.
   * @default true
   */
  expandOnClick?: MaybeGetter<boolean | undefined>;
  /**
   * The items contained in the tree.
   * @required
   */
  items: IterableProp<Item>;
  /**
   * How many time (in ms) the typeahead string is held before it is cleared
   * @default 500
   */
  typeaheadTimeout?: MaybeGetter<number>;
};

Toggle.propsAlt:
export type ToggleProps = {
  /**
   * The value for the Toggle.
   *
   * When passing a getter, it will be used as source of truth,
   * meaning that the value only changes when the getter returns a new value.
   *
   * Otherwise, if passing a static value, it'll serve as the default value.
   *
   *
   * @default false
   */
  value?: MaybeGetter<boolean>;
  /**
   * Called when the value is supposed to change.
   */
  onValueChange?: (value: boolean) => void;

  /**
   * If `true`, prevents the user from interacting with the input.
   *
   * @default false
   */
  disabled?: MaybeGetter<boolean | undefined>;
};

Tabs.propsAlt:
export type TabsProps<T extends string = string> = {
  /**
   * If `true`, the value will be changed whenever a trigger is focused.
   *
   * @default true
   */
  selectWhenFocused?: MaybeGetter<boolean | undefined>;
  /**
   * If the the trigger selection should loop when navigating with the arrow keys.
   *
   * @default true
   */
  loop?: MaybeGetter<boolean | undefined>;
  /**
   * The orientation of the tabs.
   *
   * @default "horizontal"
   */
  orientation?: MaybeGetter<"horizontal" | "vertical" | undefined>;
  /**
   * The default value for `tabs.value`
   *
   * When passing a getter, it will be used as source of truth,
   * meaning that `tabs.value` only changes when the getter returns a new value.
   *
   * If omitted, it will use the first tab as default.
   *
   * @default undefined
   */
  value?: MaybeGetter<T | undefined>;
  /**
   * Called when the `Tabs` instance tries to change the active tab.
   */
  onValueChange?: (active: T) => void;
};

Slider.propsAlt:
export type SliderProps = {
  /**
   * The minimum value of the slider.
   *
   * @default 0
   */
  min?: MaybeGetter<number | undefined>;
  /**
   * The maximum value of the slider.
   *
   * @default 100
   */
  max?: MaybeGetter<number | undefined>;
  /**
   * The orientation of the slider.
   *
   * @default "horizontal"
   */
  orientation?: MaybeGetter<"horizontal" | "vertical" | undefined>;

  /**
   * The step size of the slider.
   *
   * @default 1
   */
  step?: MaybeGetter<number | undefined>;
  /**
   * The default value for `tabs.value`
   *
   * When passing a getter, it will be used as source of truth,
   * meaning that `tabs.value` only changes when the getter returns a new value.
   *
   * If omitted, it will use the first tab as default.
   *
   * @default undefined
   */
  value?: MaybeGetter<number | undefined>;
  /**
   * Called when the `Slider` instance tries to change the active tab.
   */
  onValueChange?: (active: number) => void;
};

