Select.propsAlt:
export type SelectProps<T extends string, Multiple extends boolean = false> = PopoverProps & {
  /**
   * If `true`, multiple options can be selected at the same time.
   *
   * @default false
   */
  multiple?: MaybeGetter<Multiple | undefined>;

  /**
   * The value for the Select.
   *
   * When passing a getter, it will be used as source of truth,
   * meaning that the value only changes when the getter returns a new value.
   *
   * Otherwise, if passing a static value, it'll serve as the default value.
   *
   *
   * @default false
   */
  value?: MaybeMultiple<T, Multiple>;
  /**
   * Called when the value is supposed to change.
   */
  onValueChange?: OnMultipleChange<T, Multiple>;

  /**
   * If the popover visibility should be controlled by the user.
   *
   * @default false
   */
  forceVisible?: MaybeGetter<boolean | undefined>;
};

RadioGroup.propsAlt:
export type RadioGroupProps = {
  /**
   * If `true`, prevents the user from interacting with the group.
   *
   * @default false
   */
  disabled?: MaybeGetter<boolean | undefined>;
  /**
   * If `true`, indicates that the user must select a radio button before
   * the owning form can be submitted.
   *
   * @default false
   */
  required?: MaybeGetter<boolean | undefined>;
  /**
   * If the the button selection should loop when navigating with the arrow keys.
   *
   * @default true
   */
  loop?: MaybeGetter<boolean | undefined>;
  /**
   * If `true`, the value will be changed whenever a button is focused.
   *
   * @default true
   */
  selectWhenFocused?: MaybeGetter<boolean | undefined>;
  /**
   * The orientation of the slider.
   *
   * @default "vertical"
   */
  orientation?: MaybeGetter<"horizontal" | "vertical" | undefined>;
  /**
   * Input name for radio group.
   */
  name?: MaybeGetter<string | undefined>;
  /**
   * Default value for radio group.
   *
   * @default ""
   */
  value?: MaybeGetter<string | undefined>;
  /**
   * Called when the radio button is clicked.
   */
  onValueChange?: (active: string) => void;
};

Progress.propsAlt:
export type ProgressProps = {
  /**
   * The value for the progress.
   * 
   * @default undefined
   */
  value?: MaybeGetter<number | undefined>;

  /**
   * The maximum value of the progress.
   * 
   * @deafult 100
   */
  max?: MaybeGetter<number | undefined>;

  /**
   * The callback invoked when the value of the progress changes.
   */
  onValueChange?: (value: number) => void;
};

Popover.propsAlt:
export type PopoverProps = {
  /**
   * If the Popover is open.
   *
   * When passing a getter, it will be used as source of truth,
   * meaning that the value only changes when the getter returns a new value.
   *
   * Otherwise, if passing a static value, it'll serve as the default value.
   *
   *
   * @default false
   */
  open?: MaybeGetter<boolean>;

  /**
   * Called when the value is supposed to change.
   */
  onOpenChange?: (value: boolean) => void;

  /**
   * If the popover visibility should be controlled by the user.
   *
   * @default false
   */
  forceVisible?: MaybeGetter<boolean | undefined>;

  /**
   * Options to be passed to Floating UI's `computePosition`
   *
   * @see https://floating-ui.com/docs/computePosition
   */
  computePositionOptions?: MaybeGetter<Partial<ComputePositionConfig> | undefined>;

  /**
   * If the popover should have the same width as the trigger
   *
   * @default false
   */
  sameWidth?: MaybeGetter<boolean | undefined>;
};

