PinInput.propsAlt:
export type PinInputProps = {
  /**
   * The value for the Pin Input.
   *
   * When passing a getter, it will be used as source of truth,
   * meaning that the value only changes when the getter returns a new value.
   *
   * Otherwise, if passing a static value, it'll serve as the default value.
   *
   *
   * @default ''
   */
  value?: MaybeGetter<string | undefined>;
  /**
   * Called when the `PinInput` instance tries to change the value.
   */
  onValueChange?: (value: string) => void;

  /**
   * Calledwhen the `PinInput` instance is filled.
   */
  onComplete?: (value: string) => void;

  /**
   * The amount of digits in the Pin Input.
   *
   * @default 4
   */
  maxLength?: MaybeGetter<number | undefined>;
  /**
   * An optional placeholder to display when the input is empty.
   *
   * @default '○'
   */
  placeholder?: MaybeGetter<string | undefined>;

  /**
   * If `true`, prevents the user from interacting with the input.
   *
   * @default false
   */
  disabled?: MaybeGetter<boolean | undefined>;

  /**
   * If the input should be masked like a password.
   *
   * @default false
   */
  mask?: MaybeGetter<boolean | undefined>;

  /**
   * What characters the input accepts.
   *
   * @default 'text'
   */
  type?: MaybeGetter<"alphanumeric" | "numeric" | "text" | undefined>;

  /**
   * If `true`, allows pasting values from the clipboard.
   *
   * @default true
   */
  allowPaste?: MaybeGetter<boolean | undefined>;
};

FileUpload.propsAlt:
export type FileUploadProps<Multiple extends boolean = false> = {
  /**
   * The currently selected files.
   */
  selected?: MaybeMultiple<File, Multiple>;

  /**
   * Callback fired when selected files change
   */
  onSelectedChange?: (files: Multiple extends true ? Set<File> : File | undefined) => void;

  /**
   * Whether to accept multiple files
   * @default false
   */
  multiple?: MaybeGetter<Multiple | undefined>;

  /**
   * The accepted file types. Can be a MIME type, a MIME group, or a file extension.
   * Separate multiple types with a comma.
   * @example 'image/jpeg'
   * @example 'image/*'
   * @example '.png, .jpg, .jpeg'
   */
  accept?: MaybeGetter<string | undefined>;

  /**
   * Maximum file size in bytes
   * @default undefined
   */
  maxSize?: MaybeGetter<number | undefined>;

  /**
   * Custom validate fn. Will be called together with the original validation,
   * which takes into account the `accept` and `maxSize` props.
   */
  validate?: (file: File) => boolean;

  /**
   * Callback fired when a file fails validation
   */
  onError?: (error: FileUploadError) => void;

  /**
   * Callback fired when a file is accepted
   */
  onAccept?: (file: File) => void;
};

Collapsible.propsAlt:
export type CollapsibleProps = {
  /**
   * Whether the collapsible is disabled which prevents it from being opened.
   */
  disabled?: MaybeGetter<boolean | undefined>;

  /**
   * Whether the collapsible is open.
   */
  open?: MaybeGetter<boolean | undefined>;

  /**
   * A callback called when the value of `open` changes.
   */
  onOpenChange?: (value: boolean) => void;
};

