Toggle.propsAlt:
export type ToggleProps = {
  /**
   * The value for the Toggle.
   *
   * When passing a getter, it will be used as source of truth,
   * meaning that the value only changes when the getter returns a new value.
   *
   * Otherwise, if passing a static value, it'll serve as the default value.
   *
   *
   * @default false
   */
  value?: MaybeGetter<boolean>;
  /**
   * Called when the value is supposed to change.
   */
  onValueChange?: (value: boolean) => void;

  /**
   * If `true`, prevents the user from interacting with the input.
   *
   * @default false
   */
  disabled?: MaybeGetter<boolean | undefined>;
};

Tabs.propsAlt:
export type TabsProps<T extends string = string> = {
  /**
   * If `true`, the value will be changed whenever a trigger is focused.
   *
   * @default true
   */
  selectWhenFocused?: MaybeGetter<boolean | undefined>;
  /**
   * If the the trigger selection should loop when navigating with the arrow keys.
   *
   * @default true
   */
  loop?: MaybeGetter<boolean | undefined>;
  /**
   * The orientation of the tabs.
   *
   * @default "horizontal"
   */
  orientation?: MaybeGetter<"horizontal" | "vertical">;
  /**
   * The default value for `tabs.value`
   *
   * When passing a getter, it will be used as source of truth,
   * meaning that `tabs.value` only changes when the getter returns a new value.
   *
   * If omitted, it will use the first tab as default.
   *
   * @default undefined
   */
  value?: MaybeGetter<T>;
  /**
   * Called when the `Tabs` instance tries to change the active tab.
   */
  onValueChange?: (active: T) => void;
};

SliderMultiThumb.propsAlt:
export type SliderMultiThumbProps = {
    /**
   * The minimum value of the slider.
   *
   * @default 0
   */
  min?: MaybeGetter<number | undefined>;
  /**
   * The maximum value of the slider.
   *
   * @default 100
   */
  max?: MaybeGetter<number | undefined>;
    /**
   * The orientation of the slider.
   *
   * @default "horizontal"
   */
  orientation?: MaybeGetter<"horizontal" | "vertical" | undefined>;
  /**
   * The step size of the slider.
   *
   * @default 1
   */
  step?: MaybeGetter<number | undefined>;
    /**
   * The default value.
   *
   * When passing a getter, it will be used as source of truth,
   * meaning that `value` only changes when the getter returns a new value.
   *
   * @default undefined
   */
  value?: MaybeGetter<number[] | undefined>;
  /**
   * The direction of the slider.
   *
   * For vertical sliders, setting `dir` to `rtl`
   * causes the slider to start from the top.
   *
   * @default "ltr"
   */
  dir?: MaybeGetter<"ltr" | "rtl" | undefined>;
  /**
   * Determines if the values array should be automatically sorted.
   * 
   * @default true
   */
  autoSort?: MaybeGetter<boolean | undefined>;
    /**
   * Called when the `Slider` instance value changes.
   */
  onValueChange?: (active: number[]) => void;
};

